import React, { useState } from 'react';

function Slide() {
  const [currentSlide, setCurrentSlide] = useState(0);
  const slides = [
    { id: 1, url: 'img1.jpg' },
    { id: 2, url: 'img2.jpg' },
    { id: 3, url: 'img3.jpg' },
    { id: 4, url: 'img4.jpg' },
    { id: 5, url: 'img5.jpg' },
    { id: 6, url: 'img6.jpg' }
  ];
  const slidesToShow = 3;

  const handlePrevSlide = () => {
    setCurrentSlide(currentSlide - 1);
  };

  const handleNextSlide = () => {
    setCurrentSlide(currentSlide + 1);
  };

  return (
    <div className="slide-container">
      <div className="slide-wrapper" style={{ transform: `translateX(-${currentSlide * (100 / slidesToShow)}%)` }}>
        {slides.map(slide => (
          <div key={slide.id} className="slide-item">
            <img src={slide.url} alt={`Slide ${slide.id}`} />
          </div>
        ))}
      </div>
      <button onClick={handlePrevSlide} disabled={currentSlide === 0}>Prev</button>
      <button onClick={handleNextSlide} disabled={currentSlide === slides.length - slidesToShow}>Next</button>
    </div>
  );
}

export default Slide;
