import React, { useState } from "react";
import Dot from "../components/Dot";
import styles from "../styles/Home.module.css";
import FilteredItem from "../components/FilteredItem";
import ProjectLists from "../components/ProjectLists";

export default function Home() {
  // 우측 상단의 모집중 필터 버튼.
  const [recruitmentBtnActive, setRecruitmentBtnActive] = useState(false);

  // 필터링된 아이템을 관리하는 state.
  const [filteredItems, setFilteredItems] = useState([]);

  // 기획/개발/디자인/기타 등,
  // 추후에 db에서 가져 옴. 현재는 임시로 설정해 둠.
  const [filter, setFilter] = useState({
    기획: ["개발 기획", "서비스 기획", "프로덕트 기획", "영업 기획"],
    개발: ["웹 개발", "모바일 개발", "게임 개발", "알고리즘 개발"],
    디자인: ["건축 디자인", "인테리어 디자인", "UX/UI 디자인"],
    기타: ["일렉 기타", "어쿠스틱 기타", "클래식 기타"],
  });
  // console.log(filter.기획);

  function handleFilterClick(e) {
    if (e.target.style.backgroundColor === "rgb(112, 144, 176)") {
      e.target.style.backgroundColor = "rgba(112, 144, 176, 0.25)";
      setFilteredItems((arr) =>
        arr.filter((data) => data !== e.target.innerText)
      );
    } else {
      e.target.style.backgroundColor = "rgba(112, 144, 176, 1)";
      setFilteredItems((arr) => [...arr, e.target.innerText]);
    }
  }
  return (
    <>
      <section>
        <div className={styles.projectTitle}>
          <Dot />
          <h3>Project</h3>
        </div>
        <div className={styles.navBar}>
          <div className={styles.menus}>
            <div className={styles.menu}>기획</div>
            <div className={styles.menu}>개발</div>
            <div className={styles.menu}>디자인</div>
            <div className={styles.menu}>기타</div>
          </div>
          <div className={styles.isRecruiting}>
            <div
              className={`${styles.toggleBtn} ${
                recruitmentBtnActive && styles.active
              }`}
            >
              <div
                className={`${styles.toggleDot} ${
                  recruitmentBtnActive && styles.active
                }`}
                onClick={() => {
                  setRecruitmentBtnActive(!recruitmentBtnActive);
                }}
              ></div>
            </div>
            <div className={styles.mozipzoong}>모집중</div>
          </div>
        </div>
        <div className={styles.filterBox}>
          <div className={styles.filterFlex}>
            <div onClick={handleFilterClick}>개발 기획</div>
            <div onClick={handleFilterClick}>서비스 기획</div>
            <div onClick={handleFilterClick}>프로덕트 기획</div>
            <div onClick={handleFilterClick}>영업 기획</div>
          </div>
          <div className={styles.filteredItems}>
            <img src="/Vector.png" alt="꼬깔" />
            {filteredItems.map((filteredItem) => {
              return <FilteredItem filterName={filteredItem} />;
            })}
          </div>
        </div>
        <ProjectLists />
      </section>
    </>
  );
}
