import React, { useState } from "react";
import Dot from "../components/Dot";
import styles from "../styles/Home.module.css";

export default function Home() {
  const [recruitmentBtnActive, setRecruitmentBtnActive] = useState(false);
  function changeFilterColor(e) {
    e.target.style.backgroundColor = "rgba(112, 144, 176, 1)";
  }
  return (
    <>
      <section>
        <div className={styles.projectTitle}>
          <Dot />
          <h3>Project</h3>
        </div>
        <div className={styles.navBar}>
          <div className={styles.menus}>
            <div className={styles.menu}>기획</div>
            <div className={styles.menu}>개발</div>
            <div className={styles.menu}>디자인</div>
            <div className={styles.menu}>기타</div>
          </div>
          <div className={styles.isRecruiting}>
            <div
              className={`${styles.toggleBtn} ${
                recruitmentBtnActive && styles.active
              }`}
            >
              <div
                className={`${styles.toggleDot} ${
                  recruitmentBtnActive && styles.active
                }`}
                onClick={() => {
                  setRecruitmentBtnActive(!recruitmentBtnActive);
                }}
              ></div>
            </div>
            <div className={styles.mozipzoong}>모집중</div>
          </div>
        </div>
        <div className={styles.filterBox}>
          <div className={styles.filterFlex}>
            <div
              onClick={function (e) {
                e.target.style.backgroundColor = "rgba(112, 144, 176, 1)";
              }}
            >
              개발 기획
            </div>
            <div onClick={function (e) {}}>서비스 기획</div>
            <div onClick={function (e) {}}>프로덕트 기획</div>
            <div onClick={function (e) {}}>영업 기획</div>
          </div>
          <div className={styles.filterFlex}>
            <div>개발 기획</div>
            <div>서비스 기획</div>
            <div>프로덕트 기획</div>
            <div>영업 기획</div>
          </div>
        </div>
      </section>
    </>
  );
}
